import React, { useEffect } from'react';
import { useState } from 'react/cjs/react.development';
import './style.css';
const Weather =()=>{

    const[searchVal,setSearchVal]=useState("delhi");
    const[tempInfo,setTemInfo]=useState({});
    

    const searchData= async()=>{

      
            
        try {
            const w_data= await fetch(`https://api.openweathermap.org/data/2.5/weather?q=${searchVal}&units=metric&appid=82694a0499fb4c6e733101140a37175a`)
            const O_data= await w_data.json();
            const{temp,humidity,pressure}=O_data.main;
            const{main:weathermood}=O_data.weather[0];
            const{name}=O_data;
            const{speed}=O_data.wind;
            const{country,sunset}=O_data.sys;
            const myNewData ={temp,humidity,pressure,
           
                weathermood,name,speed,country,sunset


            };
            setTemInfo(myNewData);
            
            console.log(myNewData);

           
        } catch (error) {
            alert('Check Your City Name!!...');
        }


    };

        



    useEffect(()=>{
      
        searchData();
        
        
        
    },[]);

    let sec=tempInfo.sunset;
    let date=new Date(sec*1000);
    let timeStr=`${date.getHours()}:${date.getMinutes()}`;
    console.log(timeStr);


    return(
        <>
       
        <div className="container centerDiv fluid">
        <h4 className="text-center text-white title_div pt-2"><b>Search Your City Name..!!</b></h4>
       
        <div id="searchBar">
            <div>
                <input type="search" value={searchVal} onChange={(event)=>setSearchVal(event.target.value)} placeholder="search..."  />
               <span> <button type="button" onClick={searchData} className="btn btn-info">Search</button></span>
            </div>
        </div>
        <div className="centerDiv">

        
        <div  className="widget">

            <div className="weathe_icon">
                
            
            {tempInfo.weathermood ==='Rain'?<i class="fal fa-7x fa-cloud-showers"></i>: <i class="fad fa-7x fa-sun-cloud"></i>}
            

           

            </div>


            <div class="row">
                <div class="col-sm-8 lg-8 xl-8"> <div className="cloud_div">
                <h3>{tempInfo.temp}&deg;C</h3>
                <h5>{tempInfo.weathermood}</h5>
                <h6 className='text-white'>{tempInfo.name} , {tempInfo.country}</h6>
               
              </div>
            </div>
                <div class="col-sm-4 lg-4 xl-4"> <div className="date_div">
                <h5>{new Date().toLocaleDateString()}</h5>
                <h5>{new Date().toLocaleTimeString()}</h5>
                
             </div>
        </div>


        <div class="row">
            <div class="col col-6">
            <i class="fad fa-2x fa- fa-sun-cloud"></i>
            <span>Sunset: {timeStr}</span> </div>
            <div class="col"><i class="fal fa-2x fa-humidity"></i>
            <span>Humidity: {tempInfo.humidity}</span>
            </div>
            <div class="col"><i class="far fa-2x fa-cloud-showers-heavy"></i>
            <span> Pressure: {tempInfo.pressure}</span>
            </div>
            <div class="col"><i class="fas fa-2x fa-wind"></i>
            <span> Speed: {tempInfo.speed}{tempInfo.weathermood} </span>
            </div>
         </div>





      </div>
                 <div >
                 <p className='text-center mt-5'><b>Launched By : Er Manish Gupta</b></p>  
                </div>



        </div>
       
              
       
     
        </div>

        </div>
           
        </>
    )

}
export default Weather;
