import React, { useEffect,useState } from 'react';

const Covid = () => {
    const [data,setData]= useState([]);
    const getCovidData=async()=>{
        try{
            const res = await  fetch('https://data.covid19india.org/data.json');
            const actualData= await res.json();
            console.log(actualData.statewise[0]);
            setData(actualData.statewise[0]);
        }catch(err){
            console.log(err);

        }
      
    }

    useEffect(()=> {
    getCovidData();

    }

   ,[] );

    return(
        <div>
        <p>Live</p>
         <h2 className="text-danger">COVID 19 CORONA VIRUS TRACKER</h2>
         <table border='2'>
            <tr>
              
             
              <td><p>OUR</p></td>

            <td><span>COUNTRY</span></td>
            <td><h4>INDIA</h4></td>

            </tr>

            <tr>
            <td><p>TOTAL</p></td>
            <td><h4>RECOVERED</h4></td>

                <td><span>{data.recovered}</span></td>

            </tr>

            <tr>
            <td> <p>TOTAL</p></td>
                <td><span>{data.confirmed}</span></td>

                <td><h4>CONFIRMED</h4></td>

            </tr>

            <tr>
            <td><p>TOTAL</p></td>
                <td><h4>DEATHS</h4></td>

                <td> <span>{data.deaths}</span></td>


            </tr>

            <tr>
                <td><p>TOTAL</p></td>
                <td><h4>ACTIVE</h4></td>
                <td><span>{data.active}</span></td>

                

            </tr>

            <tr>

            <td><p>LAST</p></td>
            <td><h4>UPDATED</h4></td>
                <td><span>{data.lastupdatedtime}</span></td>

                

            </tr>

            </table>

        </div>
    )

}

export default Covid
