import React from'react';
import { useState} from 'react/cjs/react.development';
const Todo=()=>{
    const[input,setInput]= useState("");
    const[items,setItems]=useState([]);
    const[isEditItems,setEditItem]=useState();

    const addItems=()=>{
        if(!input){
            alert('pleast add list');
        }else{
            const newItems = {
                id:new Date().getTime().toString(),
                name:input,
            }
            setItems([...items,newItems]);
            setInput("");
        }

    
       
    }
    const deletItems=(index)=>{
        const updatedData= items.filter((curElem)=>{
               return  curElem.id !== index;
           });
           setItems(updatedData);
   
   
    };


    const editItems=(index)=>{
        const editated_data=items.find((curElem)=>{
            return curElem.id==index;

        });
        setInput(editated_data.name);
        setEditItem(index);

    }

    const clearData=()=>{
        return setItems([]);
    }
  

  
return (
    <>
        <div className="container fluid">
        <div className="center div">
        <figure>
        <span><i className="far fa-list-alt"></i></span>
            <figurecaption>  Add Your List Here!!</figurecaption>
        </figure>
        <div classNameName="Add items">
        <input type="text" required="required" placeholder="Add Items" value={input} onChange={(event)=>setInput(event.target.value)}  />
        <i className="fas fa-2x fa-plus-circle" onClick={addItems}></i>

        </div><br></br>
        <div classNameName="show items">
      
        {items.map((curElem)=>{
            return(<>
                <div classNameName="each Items" key={curElem.id}>
                <h6 > <span>{curElem.name} </span>
         
                <span> <i className="fas fa-edit"  onClick={()=>editItems(curElem.id)} ></i> </span>
                <span> <i className="fas fa-trash-alt" onClick={()=>deletItems(curElem.id)}></i> </span>
            </h6>
            </div>
            </>
              

            )

        })}
       

      
       

        <br></br>

        <button className="btn btn-secondary" onClick={clearData}>Remove All</button>

        </div>

        </div>

        </div>
    </>
)
}
export default Todo;
