import React from'react';
import { useState } from 'react/cjs/react.development';
const Todo=()=>{
    const[input,setInput]= useState("");
    const[items,setItems]=useState([]);

    const addItems=()=>{
     setItems([...items,input]);
       
    }

return (
    <>
        <div className="container fluid">
        <div className="center div">
        <figure>
        <span><i className="far fa-list-alt"></i></span>
            <figurecaption>  Add Your List Here!!</figurecaption>
        </figure>
        <div classNameName="Add items">
        <input type="text" placeholder="Add Items" value={input} onChange={(event)=>setInput(event.target.value)}  />
        <i className="fas fa-2x fa-plus-circle" onClick={addItems}></i>

        </div><br></br>
        <div classNameName="show items">
        <div classNameName="each Items">
        {items.map((curElem,index)=>{
            return(
                <h6>{curElem} 
         
                    <span> <i className="fas fa-edit"></i> </span>
                    <span> <i className="fas fa-trash-alt"></i> </span>
                </h6>

            )

        })}
       

      
       

        </div><br></br>

        <button className="btn btn-secondary">Check List</button>

        </div>

        </div>

        </div>
    </>
)
}
export default Todo;
