                <h4>India</h4>

            </div>

            <div>
                <p>TOTAL</p>
                <span>{data.confirmed}</span>

                <h4>CONFIRMED</h4>

            </div>

            <div>
                <p>TOTAL</p>
                <span>{data.deaths}</span>

                <h4>India</h4>

            </div>

            <div>
                <p>TOTAL</p>
                <span>{data.active}</span>

                <h4>ACTIVE</h4>

            </div>

            <div>
                <p>TOTAL</p>
                <span>{data.recovered}</span>

                <h4>India</h4>

            </div>



        </div>
    )

}

export default Covid
