import React from "react";
import { useParams } from "react-router-dom";
import Product from "../../api/ProductsApi";
import "./"

const ProductDetails = () => {
  const product_id = useParams().id;
  //console.log(product_id);
  const product_details = Product.filter((x) => x.id == product_id);

  return (
    <>
      <div className="container my-5 py-3">
        {product_details.map((item) => {
          return (
            <>
              <div className="row ">
                <div className="col-md-6 d-flex justify-content-center mx-auto " id="p_details_img" >
                  <img  src={item.img} alt={item.title} style={{height:'300px',width:'500px'}} className="img-fluid"  />
                </div>
                <div className="col-md-6 d-flex flex-column justify-content-center mx-auto">
                  <h2>{item.title}</h2>
                  <hr />
                  <h3> <span className="fw-bold m-1">â‚¹</span>{item.price}</h3>
                  <p>{item.desc}</p>
                  <button className="btn btn-outline-primary">
                    Add To Cart
                  </button>
                </div>
              </div>
            </>
          );
        })}
      </div>
    </>
  );
};

export default ProductDetails;
