import React from "react";
import Menu from"./RestApi";
import { useState } from "react";
const Nav=()=>{
    const [menuData,setMenuData]=useState(Menu);
    const filterItem=(category)=>{
     const updatedList=  Menu.filter((curElem)=>{
         return curElem.category===category;
     });

     setMenuData(updatedList);
    }

    return(
        <>
      
     <nav className="navbar container navbar-expand-lg navbar-light bg-light">
  <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarTogglerDemo03" aria-controls="navbarTogglerDemo03" aria-expanded="false" aria-label="Toggle navigation">
    <span className="navbar-toggler-icon"></span>
  </button>
  <a className="navbar-brand" href="#">SOFTMAN</a>

  <div className="collapse navbar-collapse" id="navbarTogglerDemo03">
    <ul className="navbar-nav mr-auto mt-2 mt-lg-0">
      <li className="nav-item active">
        <a className="nav-link" onClick={filterItem(()=>"breakfast")}>BreakFast <span className="sr-only">(current)</span></a>
      </li>
      <li className="nav-item">
        <a className="nav-link">Lunch</a>
      </li>
      <li className="nav-item">
        <a className="nav-link">Evening</a>
      </li>
      <li className="nav-item">
        <a className="nav-link">Dinner</a>
      </li>
      <li className="nav-item">
        <a className="nav-link">All</a>
      </li>
    </ul>
  
  </div>
</nav>
       

        </>
    )
}
export default Nav;
